package packone;

public class WrapperConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10;
		System.out.println(i);
		
		// Boxing : Value --- Refere ; Primitive --- Object
		Integer wrapinfoiobj = new Integer(i);
		
		// Unboxing : Reference ---- Value
		System.out.print(wrapinfoiobj);
		
		
		
		
		
		
		
		
		
		

	}

}
